style.value:
body {
  background-color: hsl(0, 0%, 96%);
}

h1 {
  color: #444;
  font-size: 23px;
  font-family: 'Lucida Grande','Hiragino Kaku Gothic ProN', Meiryo, sans-serif;
}

script.value:
/*
 * runstant lite
 */

$(function() {
 var sh1 = $('h1');
 //h1要素を指定

 sh1.css({
  "color":"green",
  "font-size":"35px",
  "margin-left":"50px",
  "margin-right":"0px"
 });
 //css()で変更。プロパティ名とプロパティの値を””囲うのを忘れずに。

 console.log(sh1.css("margin-left"));
 //css("プロパティ名")でプロパティを表示できます。
});
